import { useState, useEffect } from 'react'
import BlogList from './BlogList'

const Home = () => {
    const [blogs, setBlogs] = useState([
        {blog_name: 'my first entry', body: 'here it is 1...', author: 'mario', id: 1},
        {blog_name: 'my second entry', body: 'here it is 2...', author: 'yoshi', id: 2},
        {blog_name: 'my third entry', body: 'here it is 3...', author: 'bowser', id: 3}
    ]);

    const [name, setName] = useState('mario');

    const handleDelete = (id) => {
        const newBlogs = blogs.filter(blog => blog.id !== id)
        console.log(id)
        setBlogs(newBlogs);
    }

    useEffect(() => {
        console.log("Use effect ran")
        console.log(blogs)
    }, []);

    return (
    <div>
    <h1>Home</h1>

    <BlogList blogs={blogs} title="all blogs!" handleDelete={handleDelete}/>

    {/* // <BlogList blogs={blogs.filter((blog) => blog.author=== 'mario')}/> */}

    <button onClick={() => setName('jack')}>change name</button>
    </div>
    )
};

export default Home
