  //     setJoinCode(data.room)
  //     const wordColorMappingArray = await Promise.all(
  //       Object.entries(data.word_color_mapping).map(async ([word, color]) => {
  //         return { word, color };
  //       })
  //     );
  //     setWordColorMapping(wordColorMappingArray)
  //     const socket = io();
  //     socket.emit('wordColorMapping', { word_color_mapping: wordColorMappingArray },
  //       'room', joinCode);
  //   } catch (error) {
  //     console.error('Error fetching word color mapping:', error);
  //   }
  // };

  // const fetchWordsAndRenderBoard = async (joinCode) => {
  //   const retrieveData = async (joinCode) => {
  //     const response = await fetch(`http://127.0.0.1:5000/retrieve_data?room_code=${joinCode}`);
  //     const data = await response.json();
  //     console.log(data)
  //     const wordColorMappingArray = await Promise.all(
  //       Object.entries(data.word_color_mapping).map(async ([word, color]) => {
  //         return { word, color };
  //       })
  //     );
  //     setWordColorMapping(wordColorMappingArray)
  //     console.log('Retrieved data:', wordColorMappingArray);
  //   };
  //   // Call the function and pass the room code
  //   retrieveData(joinCode);
  // };

  // useEffect(() => {
  //   if (wordColorMapping.length === 25) {
  //     renderGrid();
  //   }
  // }, [fetchingData, wordColorMapping]);

  // const handleCellClick = (index) => {
  //   if (gameOver || (spymasterMode && clickedCells[index])) {
  //     return;
  //   }

  //   if (clickedCells[index]) {
  //     return; // Cell has already been clicked
  //   }

  //   if (spymasterMode) {
  //     return; // Disallow cell click in Spymaster Mode
  //   }

  //   const updatedClickedCells = [...clickedCells];
  //   updatedClickedCells[index] = true;
  //   setClickedCells(updatedClickedCells);

  //   if (wordColorMapping.length === 25) {
  //     const { color } = wordColorMapping[index];

  //     if (color === 'black') {
