import React, { useState, useEffect } from 'react';
import '../index.css';

const CodeNames = () => {
  const [wordColorMapping, setWordColorMapping] = useState([]);
  const [clickedCells, setClickedCells] = useState([]);
  const [currentTurn, setCurrentTurn] = useState('blue');
  const [blueScore, setBlueScore] = useState(0);
  const [redScore, setRedScore] = useState(0);
  const [gameOver, setGameOver] = useState(false);
  const [spymasterMode, setSpymasterMode] = useState(false);
  const [winner, setWinner] = useState(null);
  const [winnerMessage, setWinnerMessage] = useState('');
  const [fetchingData, setFetchingData] = useState(true); // Track if data is being fetched

  const BLUE_WORD_COUNT = 8;
  const RED_WORD_COUNT = 7;

  useEffect(() => {
    fetchWordColorMapping();
  }, []);

  const fetchWordColorMapping = async () => {
    try {
      console.log('Fetching word color mapping...');
      const response = await fetch('http://127.0.0.1:5000/start_codenames_game');

      if (!response.ok) {
        throw new Error(`Failed to fetch word color mapping. Status: ${response.status} - ${response.statusText}`);
      }

      console.log('Word color mapping fetched successfully.');

      const data = await response.json();
      console.log('Response:', data);

      const wordColorMappingArray = await Promise.all(
        Object.entries(data).map(async ([word, color]) => {
          return { word, color };
        })
      );
      console.log('Word Color Mapping Array:', wordColorMappingArray);
      setWordColorMapping(wordColorMappingArray);
      setClickedCells(new Array(25).fill(false));
      console.log('Fetching word color mapping completed.');
      setFetchingData(false); // Finish fetching data
    } catch (error) {
      console.error('Error fetching word color mapping:', error);
    }
  };

  const handleCellClick = (index) => {
    if (gameOver || (spymasterMode && clickedCells[index])) {
      return;
    }

    if (clickedCells[index]) {
      return; // Cell has already been clicked
    }

    if (spymasterMode) {
      return; // Disallow cell click in Spymaster Mode
    }

    const updatedClickedCells = [...clickedCells];
    updatedClickedCells[index] = true;
    setClickedCells(updatedClickedCells);

    if (!fetchingData && wordColorMapping.length === 25) {
      const { color } = wordColorMapping[index];

      if (color === 'black') {
        if (currentTurn === 'blue') {
          setWinner('red');
          setWinnerMessage('Red Wins!');
        } else {
          setWinner('blue');
          setWinnerMessage('Blue Wins!');
        }
        setGameOver(true);
      } else if (color === 'blue') {
        setBlueScore(blueScore + 1);
        if (blueScore + 1 === BLUE_WORD_COUNT) {
          setWinner('blue');
          setWinnerMessage('Blue Wins!');
          setGameOver(true);
        }
      } else if (color === 'red') {
        setRedScore(redScore + 1);
        if (redScore + 1 === RED_WORD_COUNT) {
          setWinner('red');
          setWinnerMessage('Red Wins!');
          setGameOver(true);
        }
      }
      if (currentTurn !== color && color !== 'black') {
        setCurrentTurn((prevTurn) => (prevTurn === 'blue' ? 'red' : 'blue'));
      }
    }
  };

  const handleNewGame = () => {
    setFetchingData(true); // Start fetching data
    fetchWordColorMapping();
    setCurrentTurn('blue');
    setBlueScore(0);
    setRedScore(0);
    setGameOver(false);
  };

  const handleToggleSpymasterMode = () => {
    setSpymasterMode(!spymasterMode);
  };

  const calculateRemainingWords = (teamScore, totalWords) => {
    return totalWords - teamScore;
  };

  const renderGrid = () => {
    if (fetchingData) {
      return null; // Return null when data is being fetched
    }

    if (wordColorMapping.length !== 25) {
      return null; // Return null if wordColorMapping is not yet populated
    }

    const grid = [];
    let rowIndex = 0;

    for (let i = 0; i < 25; i += 5) {
      const row = [];

      for (let j = i; j < i + 5; j++) {
        const { word, color } = wordColorMapping[j];

        let cellStyle = {
          backgroundColor: 'white',
          boxShadow: '2px 2px 4px rgba(0, 0, 0, 0.2)',
        };

        if (spymasterMode) {
          cellStyle.backgroundColor =
            color === 'neutral' ? (word === 'black' ? 'black' : 'lightgrey') : color;
          cellStyle.color = 'white';
        } else if (clickedCells[j]) {
          cellStyle.backgroundColor =
            color === 'neutral' ? (word === 'black' ? 'black' : 'lightgrey') : color;
          cellStyle.color = 'white';
        }

        row.push(
          <div
            key={j}
            className="box"
            style={cellStyle}
            onClick={() => handleCellClick(j)}
          >
            {word}
          </div>
        );
      }

      grid.push(
        <div key={rowIndex} className="grid-row">
          {row}
        </div>
      );

      rowIndex++;
    }

    return grid;
  };

  const remainingBlueWords = calculateRemainingWords(blueScore, BLUE_WORD_COUNT);
  const remainingRedWords = calculateRemainingWords(redScore, RED_WORD_COUNT);

  return (
    <div className="codenames">
      <h1>Code Names</h1>

      <div>
        <h4>
          Remaining Words<br />
          Blue: {remainingBlueWords} | Red: {remainingRedWords}
        </h4>

        <div className="codenames-board">
          <div className="codenames-grid">{renderGrid()}</div>
        </div>

        <div>
          <button onClick={handleNewGame}>New Game</button>
          <button onClick={handleToggleSpymasterMode}>Spymaster Mode</button>
        </div>
        {!gameOver && <h4>Turn: {currentTurn}</h4>}
        {gameOver && <h4>{winnerMessage}</h4>}
      </div>
    </div>
  );
};

export default CodeNames;
