import { useState } from "react";
import { useNavigate } from "react-router-dom"
import { Editor } from "primereact/editor"

const CreateEntry = () => {
    const [title, setTitle] = useState('');
    const [category, setCategory] = useState('');
    const [body, setBody] = useState('');
    const [author, setAuthor] = useState('Ryan');
    // const [rawDate, setEntryDate] = useState(Date())
    const currentDate = new Date();
    const entryDate = currentDate.toLocaleDateString('en-US', { month: 'long', day: 'numeric', year: 'numeric' });
    const [isPending, setIsPending] = useState(false);
    const navigate = useNavigate();
    // const [selectedFile, setSelectedFile] = useState(null);

    const importantInfo = () => {
        console.log(title)
        console.log(body)
    }

    const handleSubmit = (e) => {
       e.preventDefault()
       const blog = { title, body, author, category, entryDate }
       setIsPending(true)

       fetch('http://localhost:8000/blogs', {
        method: 'POST',
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(blog)
       }).then(() => {
        console.log(blog)
        console.log("New blog added")
        setIsPending(false)
        navigate('/');
       })
    }

    return (
        <div className="create_entry">
        <h2>Create Something New!</h2>


        <button onClick={importantInfo}>
            Click Me
        </button>

        <form onSubmit={handleSubmit}>
            <label>Entry title:</label>
            <input
            type="text"
            required
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            />

            <label>Category:</label>
            <input
            type="text"
            required
            value={category}
            onChange={(e) => setCategory(e.target.value)}
            />

            <label>Entry body:</label>
            <div className="body">
            <Editor
            value = {body}
            onTextChange={(e) => setBody(e.htmlValue)}
            required
            style = {{height: "300px"}}>
            </Editor>
            </div>

            {/* <label>Add Image</label>
            <input
            type="file"
            value={selectedFile}
            onChange={(e) => setSelectedFile(e.target.files[0])}
            /> */}

            { !isPending && <button>Submit</button>}
            { isPending && <button disabled>Adding Entry...</button>}
        </form>

        </div>
    );
}

export default CreateEntry;
