import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const SocketingComponent = ({ wordColorMapping }) => {
    const [joinCode, setJoinCode] = useState('');
    const [blueScore, setBlueScore] = useState(0);
    const [redScore, setRedScore] = useState(0);

    useEffect(() => {
        const socket = io('http://127.0.0.1:5000');

        socket.on('connect', () => {
            console.log('Connected to the server');
            socket.emit('joinGame', { room: joinCode }); // Emit the 'joinGame' event with the entered code
        });

        const dictionaryWordColorMapping = wordColorMapping.reduce((acc, { word, color }) => {
            acc[word] = color;
            return acc;
        }, {});

        socket.emit('wordColorMapping', { word_color_mapping: dictionaryWordColorMapping });

        socket.on('blueScore', (data) => {
            setBlueScore(data.blueScore);
        });

        socket.on('redScore', (data) => {
            setRedScore(data.redScore);
        });

        // Clean up the socket connection when the component unmounts
        return () => {
            socket.disconnect();
        };
    }, [joinCode, wordColorMapping]);

    return <div></div>;
};

export default SocketingComponent;
