import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';

const SocketingComponent = () => {
    useEffect(() => {
        const socket = io('http://127.0.0.1:5000');

        socket.on('connect', () => {
            console.log('Connected to the server');
            socket.emit('joinGame', { room: joinCode }); // Emit the 'joinGame' event with the entered code
        });

        socket.on('wordColorMapping', (data) => {
            setWordColorMapping(data.wordColorMapping);
        });

        socket.on('blueScore', (data) => {
            setBlueScore(data.blueScore);
        });

        socket.on('redScore', (data) => {
            setRedScore(data.redScore);
        });

        // Clean up the socket connection when the component unmounts
        return () => {
            socket.disconnect();
        };
    }, []);

    return <div></div>;
};

export default SocketingComponent;
