import useFetch from './useFetch';

const ChatRoom = () => {

    return (
    <div className="chatroom">
    <h1>Chat Room</h1>

    <form method="post" class="button">
        <h3>Enter The Chat Room</h3>
        <div>
            <label>Name:</label>
            <input type="text" placeholder="Pick a name!" name="name"/>
        </div>
        <div class="join">
            <input type="text" placeholder="Room Code" name="code"></input>
            <button type="submit" name="join" class="join_room"> Join a room</button>
        </div>
        <button class="chatroom_create" type="submit" name="create">Create a Room</button>
        </form>
    <form>Create a room</form>
    <form>Join a room</form>
    </div>
    );
};

export default ChatRoom;
