import React, { useState, useEffect } from 'react';
import '../index.css';
import SocketingComponent from './SocketingComponent';
import io from 'socket.io-client';


const CodeNames = () => {
  let [wordColorMapping, setWordColorMapping] = useState([]);
  let [words, setWords] = useState([]);
  const [codeEntered, setCodeEntered] = useState('');
  const [joinCode, setJoinCode] = useState('');
  const [clickedCells, setClickedCells] = useState([]);
  const [currentTurn, setCurrentTurn] = useState('blue');
  const [blueScore, setBlueScore] = useState(0);
  const [redScore, setRedScore] = useState(0);
  const [gameStarted, setGameStarted] = useState(false)
  const [gameOver, setGameOver] = useState(false);
  const [spymasterMode, setSpymasterMode] = useState(false);
  const [winner, setWinner] = useState(null);
  const [winnerMessage, setWinnerMessage] = useState('');
  const [fetchingData, setFetchingData] = useState(true);
  const [initiateSocketingComponent, setInitiateSocketingComponent] = useState(false);
  const [showTest, setShowTest] = useState(false)

  const BLUE_WORD_COUNT = 8;
  const RED_WORD_COUNT = 7;

  const createGame = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/create_new_game`)
      if (!response) {
        throw new Error('Create game didnt work')
      }
      const data = await response.json();
      setWordColorMapping(data);
    }
    catch (error) {
      console.log("There was an error with create new game")
    }
  }

  const joinGame = async () => {
    try {
      const response = await fetch(`http://127.0.0.1:5000/join_game/${joinCode}`)
      if (!response) {
        throw new Error('Join didnt work')
      }
      const data = await response.json();
      setWordColorMapping(data);
      setWords(Object.keys(wordColorMapping['word_color_mapping']))

    }
    catch (error) {
      console.log("There was an error with join game")
    }
  }

  useEffect(() => {
    if (wordColorMapping.length !== 0) {
      console.log("Game info retrieved from server")
      setWords(Object.keys(wordColorMapping['word_color_mapping']))
    }
  }, [wordColorMapping]);

  useEffect(() => {
    if (words.length === 25) {
      console.log("Words list built")
      // renderGrid()
    }
  }, [words])

  // const renderGrid = () => {
  //   console.log("We are making a grid");
  //   const grid = [];
  //   let rowIndex = 0;
  //   for (let i = 0; i < 25; i += 5) {
  //     let row = [];
  //     for (let j = i; j < i + 5; j++) {
  //       let word = words[j];
  //       console.log(word)
  //       let clicked = wordColorMapping['word_color_mapping'][word]['clicked'];
  //       let color = wordColorMapping['word_color_mapping'][word]['color'];
  //       let position = wordColorMapping['word_color_mapping'][word]['position'];
  //       let cellStyle = {
  //         backgroundColor: 'white',
  //         boxShadow: '2px 2px 4px rgba(0, 0, 0, 0.2)',
  //       };
  //       if (spymasterMode || clicked) {
  //         cellStyle.backgroundColor =
  //           color === 'neutral' ? (color === 'black' ? 'black' : 'lightgrey') : color;
  //         cellStyle.color = 'white';
  //       }
  //       row.push(
  //         <div key={word} className="box" style={cellStyle}>
  //           {words[j]}
  //         </div>
  //       );
  //     }
  //     grid.push(
  //       <div key={rowIndex} className="grid-row">
  //         {row}
  //       </div>
  //     );
  //     rowIndex++;
  //   }
  //   return grid;
  // };

  function createGrid() {
    const numRows = 5;
    const numColumns = 5;
    const grid = [];
    // Loop through the rows
    for (let row = 0; row < numRows; row++) {
      const rowCells = [];
      for (let column = 0; column < numColumns; column++) {
        const key = `cell-${row}-${column}`;
        // Create the cell JSX element
        const cell = <div key={key} className="cell"></div>;
        // Add the cell to the current row's array
        rowCells.push(cell);
      }
      // Create the row JSX element
      const rowElement = <div key={`row-${row}`} className="row">{rowCells}</div>;
      // Add the row element to the grid array
      grid.push(rowElement);
    }
    // Return the grid JSX elements
    // return <div className="grid">{grid}</div>;
    return grid
  }

  const GridComponent = () => {
    const grid = renderGrid();
    return <div className="grid-container">{grid}</div>
  }

  const handleCellClick = (index) => {
    if (gameOver || (spymasterMode && clickedCells[index])) {
      return;
    }

    if (clickedCells[index]) {
      return; // Cell has already been clicked
    }

    if (spymasterMode) {
      return; // Disallow cell click in Spymaster Mode
    }

    const updatedClickedCells = [...clickedCells];
    updatedClickedCells[index] = true;
    setClickedCells(updatedClickedCells);

    if (wordColorMapping.length === 25) {
      const { color } = wordColorMapping[index];

      if (color === 'black') {
        if (currentTurn === 'blue') {
          setWinner('red');
          setWinnerMessage('Red Wins!');
        } else {
          setWinner('blue');
          setWinnerMessage('Blue Wins!');
        }
        setGameOver(true);
      } else if (color === 'blue') {
        setBlueScore(blueScore + 1);
        if (blueScore + 1 === BLUE_WORD_COUNT) {
          setWinner('blue');
          setWinnerMessage('Blue Wins!');
          setGameOver(true);
        }
      } else if (color === 'red') {
        setRedScore(redScore + 1);
        if (redScore + 1 === RED_WORD_COUNT) {
          setWinner('red');
          setWinnerMessage('Red Wins!');
          setGameOver(true);
        }
      }
      if (currentTurn !== color && color !== 'black') {
        setCurrentTurn((prevTurn) => (prevTurn === 'blue' ? 'red' : 'blue'));
      }
    }
  };

  const handleNewGame = async () => {
    createGame();
    setShowTest(true)
    setFetchingData(true);
    setCurrentTurn('blue');
    setBlueScore(0);
    setRedScore(0);
    setGameOver(false);
    setGameStarted(true);
    // setInitiateSocketingComponent(true);
    setFetchingData(false);
  };

  const handleJoinGame = async () => {
    joinGame(joinCode)
    setShowTest(true)
    // setFetchingData(true);
    setGameStarted(true);
    // await fetchWordsAndRenderBoard(joinCode);
    // setInitiateSocketingComponent(true);

    // setFetchingData(false);
  };


  const handleToggleSpymasterMode = () => {
    console.log(clickedCells)
    setSpymasterMode(!spymasterMode);
  };

  const calculateRemainingWords = (teamScore, totalWords) => {
    return totalWords - teamScore;
  };

  const remainingBlueWords = calculateRemainingWords(blueScore, BLUE_WORD_COUNT);
  const remainingRedWords = calculateRemainingWords(redScore, RED_WORD_COUNT);

  return (

    <div className="codenames">

      <h1>Code Names</h1>

      {!gameStarted &&
        <div className="pregame">
          <div className="pregame-buttons">
            <button onClick={handleNewGame}>Create Game</button>
            <div className="join-game">
              <button onClick={handleJoinGame}>Join Game</button>
              <input className="input" type="text" placeholder="Code" name="code" onChange={(e) => setJoinCode(e.target.value)}
              />
            </div>
          </div>
        </div>
      }

      {gameStarted &&
        <div>
          <h2>Game Room: {joinCode}</h2>
          <div>
            <h4>
              Remaining Words<br />
              Blue: {remainingBlueWords} | Red: {remainingRedWords}
            </h4>
            <div className="codenames-board">
              {words.length === 25 &&
                <div className="codenames-grid">{createGrid()}</div>}
            </div>
            <div>
              <button onClick={handleNewGame}>Create Game</button>
              <button onClick={handleToggleSpymasterMode}>Spymaster Mode</button>
              {initiateSocketingComponent && <SocketingComponent wordColorMapping={wordColorMapping} />}
            </div>
            {!gameOver && <h4>Turn: {currentTurn}</h4>}
            {gameOver && <h4>{winnerMessage}</h4>}
          </div>
        </div>
      }
    </div>
  );
};

export default CodeNames;
