import React, { useState, useEffect } from 'react';
import '../index.css';

const CodeNames = () => {
const [wordColorMapping, setWordColorMapping] = useState([]);
const [clickedCells, setClickedCells] = useState(Array(25).fill(false));

  useEffect(() => {
    fetchWordColorMapping();
  }, []);

  const fetchWordColorMapping = async () => {
    try {
      const response = await fetch('http://127.0.0.1:5000/start_codenames_game');
      const data = await response.json();
      console.log('Response:', data);

      const wordColorMappingArray = Object.entries(data).map(([word, color]) => ({ word, color }));
      setWordColorMapping(wordColorMappingArray);
    } catch (error) {
      console.error(error);
    }
  };

  const handleCellClick = (index) => {
    setClickedCells((prevClickedCells) => {
      const updatedClickedCells = [...prevClickedCells];
      updatedClickedCells[index] = true;
      return updatedClickedCells;
    });
  };

  const renderGrid = () => {
    if (wordColorMapping.length !== 25) {
      return null;
    }

    const grid = [];
    let rowIndex = 0;

    for (let i = 0; i < 25; i += 5) {
      const row = [];

      for (let j = i; j < i + 5; j++) {
        const { word, color } = wordColorMapping[j];

        const cellStyle = {
          backgroundColor: clickedCells[j] ? color : 'transparent',
        };

        row.push(
          <div
            key={j}
            className="box"
            style={cellStyle}
            onClick={() => handleCellClick(j)}
          >
            {word}
          </div>
        );
      }

      grid.push(
        <div key={rowIndex} className="grid-row">
          {row}
        </div>
      );

      rowIndex++;
    }

    return grid;
  };

  return (
    <div className="codenames">
      <h1>Code Names</h1>
      <body>
        <div className="grid">{renderGrid()}</div>
      </body>
      <button onClick={fetchWordColorMapping}>New Game</button>
      <button>Spymaster Mode</button>
    </div>
  );
};

export default CodeNames;
