import React, { useState, useEffect } from 'react';
import '../index.css';

const CodeNames = () => {
    const [wordColorMapping, setWordColorMapping] = useState([]);

    useEffect(() => {
      fetchWordColorMapping();
    }, []);

    const fetchWordColorMapping = async () => {
      try {
        const response = await fetch('/start_codenames_game'); // Update the API URL
        const data = await response.json();
        setWordColorMapping(data);
      } catch (error) {
        console.error(error);
      }
    };

    const renderGrid = () => {
      if (wordColorMapping.length !== 25) {
        return null; // Return null if the data is not yet fetched
      }

      const grid = [];
      let rowIndex = 0;

      for (let i = 0; i < 25; i += 5) {
        const row = [];

        for (let j = i; j < i + 5; j++) {
          const word = wordColorMapping[j].word;
          const color = wordColorMapping[j].color;

          row.push(
            <div key={j} className={`box ${color}`}>
              {word}
            </div>
          );
        }

        grid.push(
          <div key={rowIndex} className="grid-row">
            {row}
          </div>
        );

        rowIndex++;
      }

      return grid;
    };

    return (
      <div className="codenames">
        <h1>Code Names</h1>

        <div className="grid">
          {renderGrid()}
        </div>

        <button onClick={fetchWordColorMapping}>New Game</button>
        <button>Spymaster Mode</button>
      </div>
    );
  };

  export default CodeNames;
