import { useState, useEffect } from 'react'
import BlogList from './BlogList'

const Home = () => {
    const [blogs, setBlogs] = useState(null);
    const [isPending, setIsPending] = useState(true);

    // Runs first part on render. Then watches "name" and only runs the function if it's changed
     useEffect(() => {
        // We fetch the data
        fetch('http://localhost:8000/blogs')
            .then(res => {
                //Response object
                console.log(res)
                if (!res.ok) {
                    throw Error('could not fetch the data')
                }
                return res.json()
            })
            .then(data => {
                // getting the data
                console.log(data);
                setBlogs(data)
                setIsPending(false)
            })
            // this catches any network error and fires a function
            .catch(err => {console.log(err.message)});
    });

    return (
    <div>
    { isPending && <div>Loading...</div>}
    <h1>Home</h1>    
    {/* This blogs && says validate the left before the right. So, it does not output the second part unless the left part = true */}
    {blogs && <BlogList blogs={blogs} title="all blogs!"/>}

    {/* // <BlogList blogs={blogs.filter((blog) => blog.author=== 'mario')}/> */}

    </div>
    )
};

export default Home
